package SOLID;

public class Compra {
	
	
	private String endereco;
	private double valor;

	public Compra(String endereco, double valor) {
		this.endereco = endereco;
		this.valor = valor;
		// TODO Auto-generated constructor stub
	}
	
	public String getEndereco() {
		return endereco;
	}
	
	public double getValor() {
		return valor;
	}

}
